SELECT r.area_name, SUM(rs.tot_p) AS total_muslim_population FROM religion_stats rs JOIN regions r ON rs.state=r.state JOIN religions rel ON rs.religion_id=rel.id JOIN tru t ON rs.tru_id=t.id WHERE rel.religion_name='Muslim' AND t.name='Total' GROUP BY r.area_name ORDER BY total_muslim_population DESC LIMIT 1;
SELECT r.area_name, SUM(rs.tot_p) AS total_christian_population FROM religion_stats rs JOIN regions r ON rs.state=r.state JOIN religions rel ON rs.religion_id=rel.id JOIN tru t ON rs.tru_id=t.id WHERE rel.religion_name='Christian' AND t.name='Total' GROUP BY r.area_name ORDER BY total_christian_population DESC LIMIT 1;
SELECT r.area_name, SUM(rs.tot_p) AS total_sikh_population FROM religion_stats rs JOIN regions r ON rs.state=r.state JOIN religions rel ON rs.religion_id=rel.id JOIN tru t ON rs.tru_id=t.id WHERE rel.religion_name='Sikh' AND t.name='Total' GROUP BY r.area_name ORDER BY total_sikh_population DESC LIMIT 1;
