SELECT r.area_name, SUM(l.person) AS urdu_speakers FROM language_stats l JOIN regions r ON l.state=r.state JOIN languages lang ON l.language_id=lang.id JOIN tru t ON l.tru_id=t.id WHERE lang.name='Urdu' AND t.name='Total' GROUP BY r.area_name ORDER BY urdu_speakers DESC LIMIT 1;
SELECT r.area_name, SUM(l.person) AS malayalam_speakers FROM language_stats l JOIN regions r ON l.state=r.state JOIN languages lang ON l.language_id=lang.id JOIN tru t ON l.tru_id=t.id WHERE lang.name='Malayalam' AND t.name='Total' GROUP BY r.area_name ORDER BY malayalam_speakers DESC LIMIT 1;
SELECT r.area_name, SUM(l.person) AS punjabi_speakers FROM language_stats l JOIN regions r ON l.state=r.state JOIN languages lang ON l.language_id=lang.id JOIN tru t ON l.tru_id=t.id WHERE lang.name='Punjabi' AND t.name='Total' GROUP BY r.area_name ORDER BY punjabi_speakers DESC LIMIT 1;
SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Gujarat' AND l.name = 'Gujarati' AND t.name = 'Total';
SELECT r.area_name, SUM(ls.person) AS odia_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Odia' AND t.name = 'Total' GROUP BY r.area_name ORDER BY odia_speakers DESC LIMIT 1;
