SELECT SUM(rs.f_lit) AS literate_muslim_women FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Muslim' AND r.area_name = 'India' AND t.name = 'Total';
SELECT r.area_name, SUM(rs.f_lit) AS literate_hindu_women FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Hindu' AND t.name = 'Total' GROUP BY r.area_name ORDER BY literate_hindu_women DESC LIMIT 1;
