SELECT rs.p_ill FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Rajasthan' AND rel.religion_name = 'Jain' AND t.name = 'Total';
SELECT rel.religion_name, (SUM(rs.f_lit) * 100.0 / NULLIF(SUM(rs.tot_f), 0)) as female_literacy_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Uttar Pradesh' AND t.name = 'Total' GROUP BY rel.religion_name ORDER BY female_literacy_rate ASC LIMIT 1;
SELECT rs.tot_m FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Punjab' AND rel.religion_name = 'Sikh' AND t.name = 'Rural';
