SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Jammu & Kashmir' AND l.name = 'Kashmiri' AND t.name = 'Total';
SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Sikkim' AND l.name = 'Nepali' AND t.name = 'Total';
SELECT r.area_name, SUM(ls.person) AS sindhi_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Sindhi' AND t.name = 'Total' GROUP BY r.area_name ORDER BY sindhi_speakers DESC LIMIT 1;
SELECT SUM(ls.person) AS total_dogri_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Dogri' AND r.area_name = 'India' AND t.name = 'Total';
SELECT r.area_name, ABS((SUM(rs.m_lit)*100.0/NULLIF(SUM(rs.tot_m),0)) - (SUM(rs.f_lit)*100.0/NULLIF(SUM(rs.tot_f),0))) as literacy_gap FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Muslim' AND t.name = 'Total' GROUP BY r.area_name ORDER BY literacy_gap DESC LIMIT 1;
SELECT r.area_name, (SUM(rs.tot_work_f) * 100.0 / NULLIF(SUM(rs.tot_f), 0)) as female_work_participation FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Christian' AND t.name = 'Total' GROUP BY r.area_name ORDER BY female_work_participation DESC LIMIT 1;
