### SQL
SELECT r.area_name AS state_name, SUM(p.persons) AS urban_population FROM population_stats p JOIN regions r ON p.state = r.state JOIN tru t ON p.tru_id = t.id WHERE t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_population DESC LIMIT 5;

