### SQL
SELECT r.area_name AS state_name, t.name AS area_type, p.age, SUM(p.persons) AS population FROM population_stats p JOIN regions r ON p.state = r.state JOIN tru t ON p.tru_id = t.id GROUP BY r.area_name, t.name, p.age ORDER BY r.area_name, t.name, p.age;

