### SQL
SELECT r.area_name AS state_name, SUM(p.persons) AS rural_children FROM population_stats p JOIN regions r ON p.state = r.state JOIN tru t ON p.tru_id = t.id WHERE t.name = 'Rural' AND p.age = '0-6' GROUP BY r.area_name ORDER BY rural_children DESC LIMIT 1;

