### SQL
WITH rural_totals AS (SELECT state, SUM(persons) AS total_persons FROM population_stats p JOIN tru t ON p.tru_id = t.id WHERE t.name = 'Rural' GROUP BY state), ranked_ages AS (SELECT p.state, p.age, SUM(p.persons) * 1.0 / rt.total_persons AS share, ROW_NUMBER() OVER (PARTITION BY p.state ORDER BY SUM(p.persons) DESC) AS rn FROM population_stats p JOIN tru t ON p.tru_id = t.id JOIN rural_totals rt ON p.state = rt.state WHERE t.name = 'Rural' GROUP BY p.state, p.age, rt.total_persons) SELECT r.area_name AS state_name, age FROM ranked_ages ra JOIN regions r ON ra.state = r.state WHERE rn = 1;

