### SQL
SELECT r.area_name, SUM(p.persons) AS rural_pop FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Rural' GROUP BY r.area_name ORDER BY rural_pop DESC;

