### SQL
SELECT r.area_name, SUM(p.persons) AS total_population FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY total_population DESC;

