### SQL
SELECT r.area_name, SUM(p.persons) AS teen_population FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' AND p.age IN ('10-14','15-19') GROUP BY r.area_name ORDER BY teen_population DESC;

