### SQL
SELECT r.area_name, SUM(p.males) AS urban_males FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Urban' GROUP BY r.area_name ORDER BY urban_males DESC;

