### SQL
SELECT r.area_name FROM population_stats p JOIN regions r ON p.state=r.state JOIN tru t ON p.tru_id=t.id WHERE t.name='Total' GROUP BY r.area_name ORDER BY ABS(SUM(CASE WHEN p.age='0-6' THEN p.persons ELSE 0 END)-SUM(CASE WHEN p.age IN ('60+','65+') THEN p.persons ELSE 0 END)) DESC;

