### SQL
SELECT r.area_name, SUM(l.person) AS punjabi_speakers FROM language_stats l JOIN regions r ON l.state=r.state JOIN languages lang ON l.language_id=lang.id JOIN tru t ON l.tru_id=t.id WHERE lang.name='Punjabi' AND t.name='Total' GROUP BY r.area_name ORDER BY punjabi_speakers DESC LIMIT 1;

