### SQL
SELECT r.area_name, SUM(rs.tot_p) AS urban_buddhists FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Buddhist' AND t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_buddhists DESC LIMIT 1;

