### SQL
SELECT r.area_name, (SUM(rs.tot_work_f) * 100.0 / NULLIF(SUM(rs.tot_f), 0)) as female_work_participation FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Christian' AND t.name = 'Total' GROUP BY r.area_name ORDER BY female_work_participation DESC LIMIT 1;

