### SQL
SELECT r.area_name, SUM(rs.tot_m) AS jain_men FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Jain' AND t.name = 'Total' GROUP BY r.area_name ORDER BY jain_men DESC LIMIT 1;

