### SQL
SELECT r.area_name, SUM(ls.person) AS bhili_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Bhili/Bhilodi' AND t.name = 'Total' GROUP BY r.area_name ORDER BY bhili_speakers DESC LIMIT 1;

